import Google from "./img/google.png";
import React, { useState } from 'react';
import "./login.css";

const Signup= () => {
  const [form, setForm] = useState({
    fullName: '',
    username: '',
    password: ''
  });
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/auth/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ fullName: form.fullName, email: form.username, password: form.password })
    });
    const data = await response.json();
    if (response.ok) {
      console.log('User signed up');
      console.log(data);
    } else {
      console.log('Failed to sign up');
      console.log(data);
    }
  };
  const google = () => {
    window.open("http://localhost:5000/auth/google", "_self");
  };

  return (
    <div className="login">
      <h1 className="loginTitle">GAB AUTO GARAGE</h1>
      <div className="wrapper">
        <div className="in_box">
          <div className="loginButton google" onClick={google}>
            <img src={Google} alt="" className="icon" />
            Google
          </div>
          <hr className="hrStyle" />
          <input className="hrStyle_2" name="fullName" type="text" placeholder="Full Name" value={form.fullName} onChange={handleChange} />
          <input name="username" type="text" placeholder="Username" value={form.username} onChange={handleChange} />
          <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} />
          <button className="submit">Login</button>
          <a href="/register" className="bottom_login">
            New here? Create an account
          </a>
          <a href="/forgot" className="bottom_login">
            Forgot password?
          </a>
        </div>
      </div>
    </div>
  );
};

export default Signup;
