import Google from "./img/google.png";
import React, { useState } from 'react';
import "./login.css";
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const google = () => {
    window.open("http://localhost:5000/auth/google", "_self");
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: username, password })
    });
    const data = await response.json();
    if (response.ok) {
      console.log('User logged in');
      // localStorage.setItem('token', data.token)
      // const decoded = jwtDecode(data.token)
      // console.log(decoded.user)
      // console.log(data);
      navigate('/');
    } else {
      console.log('Failed to log in');
      // console.log(data);
    }
  };

  return (
    <div className="login">
      <h1 className="loginTitle">GAB AUTO GARAGE</h1>
      <div className="wrapper">
        <div className="in_box">
          <div className="loginButton google" onClick={google}>
            <img src={Google} alt="" className="icon" />
            Google
          </div>
          <hr className="hrStyle" />
          <input className="hrStyle_2" type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <button className="submit" onClick={handleSubmit}>Login</button>
          <a href="/register" className="bottom_login">
            New here? Create an account
          </a>
          <a href="/forgot" className="bottom_login">
            Forgot password?
          </a>
        </div>
      </div>
    </div>
  );
};

export default Login;
