import React, { useState, useEffect } from 'react';
import Sidebar from '../Sidebar/sidebar';

function Cars({ user }) {
    const [cars, setCars] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/dashboard/admin/cars')
            .then(response => response.json())
            .then(data => setCars(data));
    }, []);
    const deleteCar = (carId) => {
      fetch(`http://localhost:5000/dashboard/admin/cars/${carId}`, {
          method: 'DELETE',
      })
      .then(response => response.json())
      .then(() => {
          setCars(cars.filter(car => car._id !== carId));
          alert('Car with ID: ' + carId + ' deleted!');
      });
  }
  
  const updateCar = (carId, updatedCar) => {
      fetch(`http://localhost:5000/dashboard/admin/cars/${carId}`, {
          method: 'PUT',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedCar),
      })
      .then(response => response.json())
      .then(data => {
          setCars(cars.map(car => car._id === carId ? data : car));
          alert('Car with ID: ' + carId + ' updated!');
      });
  }

    return (
        <>
            <Sidebar user={user} />
            <div className="bigbox">
                <h1>Cars data</h1>
                <table className="cars-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Model</th>
                            <th>Year</th>
                            <th>Color</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cars.map(car => (
                            <tr key={car._id}>
                                <td>{car._id}</td>
                                <td>{car.model}</td>
                                <td>{car.year}</td>
                                <td>{car.color}</td>
                                <td>
                                    <button onClick={() => updateCar(car._id)}>Update Car</button>
                                    <button onClick={() => deleteCar(car._id)}>Delete Car</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default Cars;
