import React, { useEffect, useState } from 'react';
import './users.css'
import Sidebar from '../Sidebar/sidebar';

const Users = ({ user }) => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/dashboard/admin/users')
            .then(response => response.json())
            .then(data => setUsers(data));
    }, []);

    const updateUserRank = (userId, newRank) => {
        fetch(`http://localhost:5000/dashboard/admin/users/${userId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ rank: newRank }),
        })
        .then(response => response.json())
        .then(data => {
            setUsers(users.map(user => user._id === userId ? data : user));
        });
    }

    return (
        <>
            <Sidebar user={user} />
            <div className="bigbox">
                <h1>Users data</h1>
                {users.map(user => (
                    <div key={user._id}>
                        <p>ID:{user._id} | NAME:{user.displayName} | EMAIL:{user.email} | DATE:{user.createdAt} | RANK:{user.rank}</p>
                        <input type="text" onChange={e => setNewRank(e.target.value)} />
                        <button onClick={() => updateUserRank(user._id, newRank)}>Update Rank</button>
                    </div>
                ))}
            </div>
        </>
    );
}

export default Users;
