import React, { useState, useEffect } from 'react';
import './mechanic.css';
import Sidebar from '../Sidebar/sidebar';

const Mechanic = ({ user }) => {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/dashboard/mechanic`)
      .then(response => response.json())
      .then(data => setCars(data));
  }, []);
  const changeStatus = (carId, newStatus) => {
    fetch(`http://localhost:5000/dashboard/mechanic/${carId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status: newStatus }),
    })
    .then(response => response.json())
    .then(data => {
      setCars(cars.map(car => car.id === carId ? {...car, status: newStatus} : car));
    });
  };

  return (
      <>
        <Sidebar user={user} />
        <div className="bigbox">
          <h1>Users data</h1>
          {cars.map(car => (
            <div key={car._id}>
              <p>{car.name}</p>
              <p>{car.carBrand} {car.status}</p>
              <button onClick={() => changeStatus(car.id, 'newStatus')}>Change Status</button>
            </div>
          ))}
        </div>
      </>
    );
  };
export default Mechanic;
