import Navbar from "./components/Navbar";
import "./components/navbar.css";
import "./app.css";
import Home from "./pages/HomePage/Home";
import Dashboard from "./pages/DashBoard/Main/dashboard";
import Appointment from "./pages/DashBoard/Appointments/appointment";
import Login from "./pages/Login/Login";
import Aboutus from "./pages/AboutUs/aboutus";
import Cars from "./pages/DashBoard/Cars/cars";
import Settings from "./pages/DashBoard/Settings/settings";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { useEffect, useState } from "react";

const App = () => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchUser().then((userData) => {
      setUser(userData);
      setIsLoading(false);
    });
  }, []);
  if (isLoading) {
    return <div>Loading...</div>;
  }
  useEffect(() => {
    const getUser = () => {
      fetch("http://localhost:5000/auth/login/success", {
        method: "GET",
        credentials: "include",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Credentials": true,
        },
      })
        .then((response) => {
          if (response.status === 200) return response.json();
          throw new Error("authentication has been failed!");
        })
        .then((resObject) => {
          setUser(resObject.user);
        })
        .catch((err) => {
          console.log(err);
        });
    };
    getUser();
  }, []);

  return (
    <BrowserRouter>
      <div>
        <Navbar user={user} />
        <Routes>
        <Route path="/" element={<Home user={user} />} />
        <Route path="/aboutus" element={<Aboutus/>} />
          <Route
            path="/dashboard"
            element={user ? <Dashboard /> : <Login />}
          />
          <Route
            path="/dashboard/appointment"
            element={user ? <Appointment /> : <Login />}
          />
          <Route
            path="/dashboard/settings"
            element={user ? <Settings /> : <Login />}
          />
          <Route
            path="/dashboard/cars"
            element={user ? <Cars /> : <Login />}
          />
          <Route
            path="/login"
            element={user ? <Navigate to="/" /> : <Login />}
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
};

export default App;
