import React, { useState, useEffect } from 'react';
import './mechanic.css';
import Sidebar from '../Sidebar/sidebar';

const Mechanic = ({ user }) => {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/dashboard/mechanic`)
      .then(response => response.json())
      .then(data => setCars(data));
  }, []);
  const changeStatus = (carId, newStatus) => {
    fetch(`http://localhost:5000/dashboard/mechanic/${carId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status: newStatus }),
    })
    .then(response => response.json())
    .then(data => {
      setCars(cars.map(car => car.id === carId ? {...car, status: newStatus} : car));
    });
  };

  return (
      <>
        <Sidebar user={user} />
        <div className="bigbox">
          <h1>Users data</h1>
          <table className="users-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Date Created</th>
                            <th>Rank</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user._id}</td>
                                <td>{user.displayName}</td>
                                <td>{user.email}</td>
                                <td>{user.createdAt}</td>
                                <td>{user.rank}</td>
                                <td>
                                    <div className="user-controls">
                                        <select onChange={e => setNewRank(e.target.value)}>
                                            <option value="" disabled>Select Rank</option>
                                            <option value="Client">Client</option>
                                            <option value="Mecanic">Mecanic</option>
                                            <option value="Admin">Admin</option>
                                        </select>
                                        <button onClick={() => updateUserRank(user._id, newRank)} disabled={!newRank}>Update Rank</button>
                                        <button onClick={() => deleteUser(user._id)}>Delete User</button>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
          {cars.map(car => (
            <div key={car._id}>
              <p>{car.name}</p>
              <p>{car.carBrand} {car.status}</p>
              <button onClick={() => changeStatus(car.id, 'newStatus')}>Change Status</button>
            </div>
          ))}
        </div>
      </>
    );
  };
export default Mechanic;
