import { Link } from "react-router-dom";

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem('token');
    window.open("http://localhost:5000/auth/logout", "_self");
  };
  const dashboard = () => {
    window.open("http://localhost:3000/dashboard", "_self");
  }
  const aboutUs = () => {
    window.open("http://localhost:3000/aboutus", "_self");
  }
  const user_name = () => {
    return user.displayName;
  }
  const user_image = () => {
    return user.profileImage;
  }
  return (
    <div className="navbar">
      <span className="logo">
        <Link className="service_logo" to="/">
          Service App
        </Link>
      </span>
      {user ? (
        <ul className="list">
          <li className="listItem">
            <img
              src={user_image()}
              alt=""
              className="avatar"
            />
          </li>
          <li className="link">{user_name()}</li>
          <li className="link" onClick={dashboard}>
            Dashboard
          </li>
          <li className="link" onClick={logout}>
            Logout
          </li>
          <li className="link" onClick={aboutUs}>
            About Us
          </li>
        </ul>
      ) : (
        <Link className="link" to="login">
          Login
        </Link>

      )}
    </div>
  );
};

export default Navbar;
